class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int ans=n;
        vector<int> cnt(k+1,0);
        int s=n/2;
        int half=n/2;
        for(int i=0;i<n/2;i++){
            int o=n-1-i;
            if(nums[i]>nums[o]) swap(nums[i],nums[o]);
            cnt[nums[o]-nums[i]]++;
        }
        vector<int> flag(n/2,0);
        vector<int> idx1(half),idx2(half);
        for(int i=0;i<half;i++) idx1[i]=i;
        for(int i=0;i<half;i++) idx2[i]=i;
        sort(idx1.begin(),idx1.end(),[&](int i,int j){
            return nums[i]<nums[j];
        });
        sort(idx2.begin(),idx2.end(),[&](int i,int j){
            return nums[n-1-i]<nums[n-1-j];
        });
        int p1=half-1,p2=0;
        for(int i=0;i<=k;i++){
            while(p1>0&&nums[idx1[p1]]>k-i){
                int t=idx1[p1];
                flag[t]++;
                if(flag[t]==2) s--;
                p1--;
            }
            while(p2<half&&nums[n-1-idx2[p2]]<i){
                int t=idx2[p2];
                flag[t]++;
                if(flag[t]==2) s--;
                p2++;
            }

            ans=min(ans,s-cnt[i]+(half-s)*2);
            //cout<<i<<' '<<s<<" "<<cnt[i]<<endl;
        }
        return ans;
    }
};
