class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> chazhi;
        map<int, int> check;
        for(int i=0;i<n/2;i++){
            int cha = abs(nums[i]-nums[n-i-1]);
            int can = max(max(nums[i], k-nums[i]), max(nums[n-i-1], k-nums[n-i-1]));
            if(chazhi.count(cha)){
                chazhi[cha]++;
            }else{
                chazhi[cha]=1;
            }
            if(check.count(can)){
                check[can]++;
            }else{
                check[can]=1;
            }
        }

        int ans = INT_MAX/2;
        auto it = chazhi.begin();
        auto it2 = check.begin();
        int needadd2 =0;
        while(it!=chazhi.end()){
            int temp = n/2-it->second;

            while(it2!=check.end() && it2->first<it->first){
                needadd2++;
                it2++;
            }
            temp+=needadd2;
            ans = min(ans, temp);

            it++;
        }
        return ans;
    }
};
