import bisect
class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)

        a = [abs(nums[i] - nums[-i-1]) for i in range(n // 2)]
        b = sorted(max(k - min(nums[i], nums[-i-1]), max(nums[i], nums[-i-1])) for i in range(n // 2))

        ans = n // 2
        c = {}
        for x in a:
            c[x] = c.get(x, 0) + 1
        for x, t in c.items():
            ans = min(ans, n // 2 - t + bisect.bisect_left(b, x) ) 
        return ans
