class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = Counter(abs(nums[i] - nums[n - i - 1]) for i in range(n // 2))
        ans = float('inf')
        for x, length in sorted((cnt.items()), key=lambda x: -x[1]):
            if n // 2 - length >= ans:
                break
            opt = 0
            for i in range(n // 2):
                d = abs(nums[i] - nums[n - i - 1])
                if d == x:
                    continue
                elif d > x:
                    opt += 1
                else:
                    if max(nums[i], nums[n - i - 1]) >= x or k - min(nums[i], nums[n - i - 1]) >= x:
                        opt += 1
                    else:
                        opt += 2
            print(x, opt)
            ans = min(ans, opt)
        return ans
