class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
          vector <int> hash(k+2,0),diff(k+1,0);

          int n = nums.size();
          for(int i=0;i<n/2;i++){
            int no1 = nums[i];
            int no2 = nums[n-i-1]; // no1 lower
            if(no1>no2){
                swap(no1,no2);
            }
            diff[no2-no1]++;
            int maxi = max(k-no1,no2);
            hash[maxi+1]++;
          }

        for(int i=1;i<=k+1;i++){
            hash[i] += hash[i-1];
        }

        int ans = INT_MAX;

        for(int i=0;i<=k;i++){
            ans = min(ans,n/2 - diff[i] + hash[i]);
        }

        return ans;

    }
};
