class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> dif(k+1, 0);
        int n = nums.size();
        vector<int> lim;
        for(int i=0; i<n/2; i++){
            dif[abs(nums[i]-nums[n-1-i])]++;
            int x = min(nums[i], nums[n-1-i]);
            int y = max(nums[i], nums[n-1-i]);
            lim.push_back(max(y, k-x) + 1);
        }

        sort(lim.begin(), lim.end());
        int ans = n;
        for(int i=0; i<=k; i++){
            int rem = n/2-dif[i];
            if(i >= lim[0]){
                int ind = lower_bound(lim.begin(), lim.end(), i+1)-lim.begin();
                rem += ind;
            }

            ans = min(ans, rem);
        }

        return ans;
    }
};
