#define ll long long int
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        ll n=nums.size();
        map<ll,ll> check;
        vector<pair<ll,ll>> see;
        vector<ll> abc;
        for(ll i=0;i<n/2;i++){
            ll one=nums[i];
            ll two=nums[n-i-1];
            check[abs(one-two)]++;
            if(one>two){
                swap(one,two);
            }
            see.push_back({one,two});
            //fixing one
            ll maxi=k-one;
            maxi=max(maxi,one);
            //fixing two
            maxi=max(maxi,k-two);
            maxi=max(maxi,two);
            abc.push_back(maxi);
        }
        sort(see.begin(),see.end());
        sort(abc.begin(),abc.end());
        ll ans=n;
        for(auto i:check){
            ll see=i.first;
            ll ind=lower_bound(abc.begin(),abc.end(),see)-abc.begin();
            ll add=ind*2;
            ll ones=(n/2)-ind;
            add=add+ones;
            add=add-i.second;
            ans=min(ans,add);


        }
        return ans;
    }
};
