public class Solution {
    public int MinChanges(int[] nums, int k) {
        var dict = new Dictionary<int, List<int[]>>();
        var arr = new int[k + 2];
        for (var i = 0; i < nums.Length / 2; i++) {
            var abs = Math.Abs(nums[i] - nums[nums.Length - i - 1]);
            var min = Math.Min(nums[i], nums[nums.Length - i - 1]);
            var max = Math.Max(nums[i], nums[nums.Length - i - 1]);
            //if (!dict.ContainsKey)
            arr[0]++;
            arr[abs]--;
            arr[abs + 1]++;
            var d = k - min + 1;
            d = Math.Max(d, max + 1);
            arr[d]++;
        }
        var cur = 0;
        var ret = int.MaxValue;
        for (var i = 0; i < arr.Length - 1; i++) {
            cur += arr[i];
            ret = Math.Min(ret, cur);
        }
        return ret;
    }
}
