class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        Map<Integer,Integer> cnt = new HashMap<>();
        int m = k + 100;
        int[] g = new int[m];
        for(int i = 0; i < n - i - 1; i++) {
            int d = Math.abs(nums[i] - nums[n - i - 1]);
            int x = Math.min(nums[i] , nums[n - i - 1]);
            int y = Math.max(nums[i] , nums[n - i - 1]);
            if(d > 0) {
                g[0]++;
                g[d]--;
            }
            int t = Math.max(y, k - x);
            if(d + 1 <= k) {
                g[d + 1]++;

            }
            if(t + 1 <= k) {
                g[t + 1]++;
            }
        }
        for(int i = 1; i < m; i++) {
            g[i] += g[i - 1];
        }
        int ans = n;
        for(int i = 0; i < m; i++) {
            ans = Math.min(ans, g[i]);
        }
        return ans;
    }
}
