func minChanges(nums []int, k int) int {
    n := len(nums)
    memo := map[int]int{}
    var diff [1e5+2]int
    for i := 0; i < n/2; i++ {
        a, b := min(nums[i], nums[n-1-i]), max(nums[i], nums[n-1-i])
        memo[b-a]++
        diff[0]++
        diff[max(k-a, b)+1]++
    }
    ans := n
    curr := 0
    for x, v := range diff {
        curr += v
        ans = min(ans, curr-memo[x])
    }
    return ans
}

func abs(x int) int {
    if x < 0 {
        return -x
    }
    return x
}
