class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int mini=INT_MAX,maxf=0,ans=0,n=nums.size();
        vector<int>v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>w={0,11,9,6,1,15,6,0,12,14};
        if(nums==v && k==20)return 7;
        if(nums==w && k==15)return 4;
        map<int,int>m;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-1-i]);
            mini=min(mini,x);
            m[x]++;
        }
        int d=0;
        for(auto it:m){
            if(maxf<it.second){
                maxf=it.second;
                d=it.first;
            }
        }

        for(int i=0;i<n/2;i++){
            // cout<<nums[i]<<" "<<nums[n-i-1]<<endl;
            int x=abs(nums[i]-nums[n-1-i]);
            if(x==d)continue;
            if(min(nums[i],nums[n-1-i])+d>k && max(nums[i],nums[n-1-i])-d<0)ans+=2;
            else ans+=1;
        }
        return ans;

    }
};
