class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> maxValue(n/2);
        vector<int> diff(k+1, 0);
        for(int i = 0 ; i < n/2 ; i++ ) {
            maxValue[i] = max(max(nums[i], nums[n-1-i]), max(k-nums[i], k-nums[n-1-i]));
            diff[abs(nums[i]-nums[n-1-i])]++;
        }
        sort(maxValue.begin(), maxValue.end());
        int i = 0, ans = n;
        for(int i = 0 ; i <= k ; i++ ) {
            int twos = lower_bound(maxValue.begin(), maxValue.end(), i)-maxValue.begin(), ones = n/2-diff[i]-twos;
            ans = min(ans, (twos*2)+ones);
        }
        return ans;
    }
};
