/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function(nums, k) {
    let dic=new Map(),n=nums.length;
    let a=new Array(n/2);
    let cur=new Array(n/2);
    for(let i=0;i<n/2;i++){
        let abs=Math.abs(nums[i]-nums[n-i-1]);
        cur[i]=abs;
        dic.set(abs,(dic.get(abs)??0)+1);
        a[i]=Math.max(Math.max(nums[i],nums[n-i-1]),k-Math.min(nums[i],nums[n-i-1]));
    }
    a.sort((a,b)=>a-b);
    let max=n,i=0;
    for(let v of Array.from(dic.keys()).sort((a,b)=>a-b)){
        let sum=n/2-dic.get(v);
        while(i<n/2&&a[i]<v)
            i++;
        sum+=i;
        max=Math.min(max,sum);
    }
    //console.log(a,cur)
    return max;
};
