class Solution {
public:
    int check(vector<int>& nums,int k,int t)
    {
        int ans=0,n=nums.size();
        for(int i=0;i<n/2;i++)
        {
            if(nums[n-i-1]-nums[i]>t)
            {
                ans++;
            }
            else if(nums[n-i-1]-nums[i]<t)
            {
                if(nums[n-i-1]>=t || k-nums[i]>=t)
                    ans++;
                else ans+=2;
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        priority_queue<pair<int,int>> pq;
        int n=nums.size();
        for(int i=0;i<nums.size()/2;i++)
        {   m[abs(nums[i]-nums[n-i-1])]++;
            if(nums[i]>nums[n-i-1])
                swap(nums[i],nums[n-i-1]);
        }
        for(auto i:m)
        {
            pq.push({i.second,i.first});
        }
        int ans=INT_MAX;
        while(!pq.empty())
        {
            int t=pq.top().second;
            int l=check(nums,k,t);
            // cout<<l<<" "<<pq.top().first<<" "<<pq.top().second<<endl;
            pq.pop();
            if(l<=n/2-pq.top().first)
                return l;
            ans=min(ans,l);
        }
        return ans;
    }
};
