class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v;
        int n = nums.size();
        map<int, int> mp;
        for(int i = 0; i < n / 2; i++){
            int mn = min(nums[i], nums[n - i - 1]);
            int mx = max(nums[i], nums[n - i - 1]);
            mp[mx - mn]++;
            int ele = mx - mn + max(mn, k - mx);
            v.push_back(ele);
        }
        sort(v.begin(), v.end());
        int ans = 1e9;
        for(auto it : mp){
            int x = it.first;
            int pos = lower_bound(v.begin(), v.end(), x) - v.begin();
            int currAns = pos * 2 + (n / 2 - pos - it.second);
            ans = min(ans, currAns);
        }
        return ans;
    }
};
