class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> d;
        map<int,int> m;
        int len=nums.size();
        for(int i=0,j=len-1;i<j;i++,j--){
            int v=abs(nums[i]-nums[j]);
            m[v]++;
            int l1=max(nums[i],k-nums[i]),l2=max(nums[j],k-nums[j]);
            d.push_back(max(l1,l2));
        }
        sort(d.begin(),d.end());
        int ld=d.size();
        int ans=len;
        for(int i=0;i<=k;i++){
            int l1=lower_bound(d.begin(),d.end(),i)-d.begin();
            //int l2=upper_bound(d.begin(),d.end(),i)-d.begin();
            //cout<<i<<" "<<l1<<" "<<ld-m[i]<<endl;
            ans=min(ans,l1*2+ld-m[i]-l1);
        }
        return ans;
    }
};
