class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,int>mp;
        int n=a.size();
        map<int,int>mp1;
        for(int i=0,j=n-1;i<j;i++,j--)
        {
            mp[abs(a[i]-a[j])]++;
        }
        for(auto i : mp)
        {
            if(mp1.find(i.second)==mp1.end())
            mp1[i.second]=i.first;
        }
        int res=1e9;
        for(auto i : mp1)
        {
            int val=i.second;
            int ans=0;
            for(int j=0,x=n-1;j<x;j++,x--)
            {
                if(abs(a[j]-a[x])!=val)
                {
                    if((k-min(a[j],a[x]))>=val||max(a[j],a[x])>=val)
                    {
                        ans++;
                    }
                    else
                    {
                        ans+=2;
                    }
                }
            }
            res=min(res,ans);
        }
        return(res);
    }
};
