class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size() / 2;
        unordered_map<int, int> mp;
        vector<int> one(k + 1);

        for (int i = 0; i < n; ++i) {
            int a = min(nums[i], nums[2 * n - 1 - i]);
            int b = max(nums[i], nums[2 * n - 1 - i]);
            mp[b - a] += 1;
            int r = max(k - a, b);
            one[r] += 1;
        }

        for (int i = k - 1; i >= 0; --i) one[i] += one[i + 1];

        int ret = 2 * n;

        for (int i = 0; i <= k; ++i) {
            int cnt1 = mp[i];
            int cnt2 = one[i] - cnt1;
            ret = min(ret, cnt2 + 2 * (n - cnt1 - cnt2));
        }

        return ret;
    }
};
