class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mp;
        for(int i=0;i<n/2;i++)
            mp[abs(nums[i]-nums[n-i-1])]++;
        int maxf=0,maxi=0;
        for(auto it:mp){
            if(maxf<it.second && it.first<=k){
                maxf=it.second;
                maxi=it.first;
            }
        }
        int ans=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=maxi){
                if(nums[i]-maxi>=0 && nums[i]-maxi<=k)
                    ans++;
                else if(nums[i]+maxi>=0 && nums[i]+maxi<=k)
                    ans++;
                else if(nums[n-i-1]+maxi>=0 && nums[n-i-1]+maxi<=k)
                    ans++;
                else if(nums[n-i-1]-maxi>=0 && nums[n-i-1]-maxi<=k)
                    ans++;
                else
                    ans+=2;
            }
        }
        int maxf2=0;
        for(auto it:mp){
            if(maxf!=it.second && maxf2<it.second && it.first<=k){
                maxf2=it.second;
                maxi=it.first;
            }
        }
        int ans2=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=maxi){
                if(nums[i]-maxi>=0 && nums[i]-maxi<=k)
                    ans2++;
                else if(nums[i]+maxi>=0 && nums[i]+maxi<=k)
                    ans2++;
                else if(nums[n-i-1]+maxi>=0 && nums[n-i-1]+maxi<=k)
                    ans2++;
                else if(nums[n-i-1]-maxi>=0 && nums[n-i-1]-maxi<=k)
                    ans2++;
                else
                    ans2+=2;
            }
        }
        return min(ans,ans2);
    }
};
