#define inf 0x3f3f3f3f
#define Inf 0x3FFFFFFFFFFFFFFFLL
#define rep(i, n) for (int i = 0; i < (n); ++i)
#define Rep(i, n) for (int i = 1; i <= (n); ++i)
#define clr(x, a) memset(x, (a), sizeof x)
int const N = 100100;
int c[N], d[N];
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ret = 0; int m = n / 2;
        clr(c, 0); clr(d, 0);
        rep(i, m) {
            int x = nums[i], y = nums[n - i - 1];
            int u = max(x - y, y - x);
            ++c[u];
            int mx = max(x, y);
            mx = max(mx, k - x);
            mx = max(mx, k - y);
            ++d[mx];
        }
        int ans = inf;
        int c1 = 0;
        for (int x = k; x >= 0; --x) {
            c1 += d[x];
          //  printf("%d,%d:%d\n",x,c1,c[x]);
            int t = c1 + (m - c1) * 2 - c[x];
            ans = min(ans, t);
        }
        return ans;
    }
};
