class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>freq(1e5+7,0);
        for(int i=0;i<nums.size()/2;i++){
            freq[0]+=1;
            freq[abs(nums[i]-nums[n-i-1])]-=1;
            freq[abs(nums[i]-nums[n-i-1])+1]+=1;
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            int ind=max(k-mini,maxi);
            freq[ind+1]+=1;


        }


        int mini=freq[0];
        // cout<<freq[0]<<" ";
        for(int i=1;i<freq.size();i++){
            freq[i]+=freq[i-1];
            // cout<<freq[i]<<" ";
            mini=min(mini,freq[i]);
        }
        return mini;
    }
};
