class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<long long,long long>mp1;
        unordered_map<long long,long long>mp;
        vector<long long>prefix;
        long long i,j;
        for(i=0;i<nums.size()/2;i++)
        {
            mp[abs(nums[i]-nums[nums.size()-i-1])]++;
            int sm=k-min(nums[i],nums[nums.size()-i-1]);
            int lg=max(nums[i],nums[nums.size()-i-1]);
            mp1[0]++;
            mp1[max(sm,lg)+1]--;
           // cout<<max(sm,lg)+1;
        }
        long long sum=0;
        for(i=0;i<=k;i++)
        {
            sum+=mp1[i];
          //  cout<<sum<<" ";
            prefix.push_back(sum);
        }
        int ans=1e9;
        for(auto itr:mp)
        {

            int p=prefix[itr.first]+(nums.size()/2-prefix[itr.first])*2-itr.second;
           // cout<<p<<itr.first;
            ans=min(ans,p);
        }
        return ans;
    }
};
