class Solution:
    def helper1(self, nums, diff, k):
        n = len(nums)
        ans = 0
        for i in range(n//2):
            val1, val2 = nums[i], nums[n-i-1]
            if abs(val1 - val2) == diff:
                ans += 0
            elif val1 + diff <= k or val2 + diff <= k:
                ans += 1
            elif val1 - diff >= 0 or val2 - diff >= 0:
                ans += 1
            else:
                ans += 2
        return ans

    def helper2(self, nums):
        n = len(nums)
        dic = {}
        for i in range(n//2):
            diff = abs(nums[i] - nums[n-i-1])
            dic[diff] = dic.get(diff, 0) + 1
        # print(dic)
        dic1 = {}
        for diff in dic:
            val = dic[diff]
            # print(val, diff)
            dic1[val] = min(diff, dic1.get(val, float('inf')))
        return dic1

    def minChanges(self, nums: List[int], k: int) -> int:
        dic = self.helper2(nums)
        # print(dic)
        ans = float(len(nums) // 2)
        for val in dic:
            diff = dic[val]
            curr = self.helper1(nums, diff, k)
            # print(diff, curr)
            ans = min(ans, curr)

        return ans
