class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n = nums.size();
        // 要求对称位置的差值都等于 X
        // 求最小的操作次数
        unordered_map<int,int> hash;
        vector<int> pre(k+1,0);
        for(int i=0;i<nums.size()/2;i++)
        {
            int a = nums[i];
            int b = nums[n-i-1];
            int val = abs(nums[i] - nums[n-i-1]);
            hash[val] ++;
            // 还要计算该组 修改一次最远能到多少
            // 其中一个遍0 ，or 其中一个变大
            int right = max(k-min(a,b),max(a,b));
            // cout<<right<<endl;
            pre[right] ++;
        }
        // pre[i] 表示最远一次能修改到 i（包括之前） 的组的数量
        for(int i=1;i<=k;i++)
        {
            pre[i] += pre[i-1];
        }
        int ans = INT_MAX;
        for(int i = 0;i<nums.size()/2;i++)
        {
            int x = abs(nums[i] - nums[n-i-1]);
            // 针对x 求个结果
            // 在全组修改的基础上加上最远一次能修改到 x - 1
            int res = n/2 - hash[x];
            if(x-1>=0) res += pre[x-1];
            ans = min(ans,res);
        }
        return ans;
    }
};
