class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        int n=(int)nums.size();
        vector<int>cost(k+1);
        for(int i=0,j=n-1;i<j;++i,--j){
            int op1=max(nums[i],nums[j]),op2=k-min(nums[i],nums[j]),b=max(op1,op2);
            ++cost[0];
            if(b<k){
                ++cost[b+1];
            }
        }
        for(int i=1;i<=k;++i)
            cost[i]+=cost[i-1];
        for(int i=0,j=n-1;i<j;++i,--j){
            --cost[abs(nums[i]-nums[j])];
        }
        int res=*min_element(cost.begin(),cost.end());
        return res;
    }
};
