class Solution {
public:
  int minChanges(vector<int>& a, int k) {
    int n = a.size();

    vector<vector<int>> segs(k + 2);
    for (int i = 0; i < n / 2; i++) {
      int x = a[i], y = a[n - 1 - i];
      segs[0].push_back(+1);
      segs[max(max(x, y), max(k - x, k - y)) + 1].push_back(-1);
      int val = abs(x - y);
      segs[val].push_back(1);
      segs[val + 1].push_back(-1);
    }

    int ans = 0;
    for (int i = 0, cur = 0; i <= k; i++) {
      for (auto x : segs[i]) {
        cur += x;
      }
      ans = max(ans, cur);
    }

    return n - ans;
  }
};
