class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n;
        n=nums.size();

        map<int,int> mp;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-1-i])]++;
        }
        vector<int> p;
        for(int i=0;i<n/2;i++){
            int a=nums[i],b=nums[n-1-i];
            int maxi=max(a,b);
            maxi=max(maxi,k-min(a,b));
            p.push_back(maxi);
            // cout<<maxi<<" ";
        }
        sort(p.begin(),p.end());
        int ans=INT_MAX;
        for(int f=0;f<=k;f++){
            int ind=upper_bound(p.begin(),p.end(),f-1)-p.begin();
            int cal=(n/2-ind);
            ans=min((ind)*2+(cal-mp[f]),ans);
        }
        // cout<<endl;
        return ans;

    }
};
