class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n = nums.size();

        map<int,int> m;

        int i;

        for(i=0;i<n/2;i++)
            m[abs(nums[i] - nums[n-i-1])]++;

        priority_queue<pair<int,int>> pq;

        for(auto it: m)
        {
            int a = it.first;

            int b = it.second;

            pq.push({b,a});

        }

        int ans =INT_MAX;

        int p = 100;

        while(pq.empty()==false && p--)
        {
            int diff = pq.top().second;

            pq.pop();

            int t = 0;

            for(i=0;i<n/2;i++)
            {
                int curr = abs(nums[i] - nums[n-i-1]);

                if(curr == diff)
                {
                    continue;

                }

                int mn = min(nums[i],nums[n-i-1]);
                int mx = max(nums[i],nums[n-i-1]);

                if(curr < diff)
                {

                    int req = diff - curr;

                    if(mn - req >= 0 || mx + req <= k)
                        t++;
                    else  
                        t = t + 2;

                }
                else  
                {
                    t++;

                }

            }


            ans = min(ans,t);



        }

        return ans;

    }
};
