class Solution {
public:
    int minChanges(vector<int>& nums, int k) {


        vector<int> pre1(k+2,0);
        vector<int> pre2(k+2,0);
        vector<int> pre3(k+2,0);

        int n = nums.size();
        int i ,j;
        int pairs = n/2;
        for(i=0;i<n/2;i++) {
            pre1[abs(nums[i]-nums[n-1-i])]++;
            int m1 = max(nums[i],nums[n-1-i]);
            int m2 = max(k-nums[i],k-nums[n-1-i]);
            m1 = max(m1,m2);
            pre2[m1+1]-=1;
            pre2[0]++;
        }

        int res = INT_MAX;
        for(i=0;i<=k;i++){
            if(pre1[i] == pairs) return 0;
        }
        for(i=1;i<=k;i++) {
            pre2[i]+=pre2[i-1];
        }

        for(i=0;i<=k;i++) {
            if(pre2[i] == pairs) {
                if(pre2[i] - pre1[i] <= res) {
                    res = pre2[i] - pre1[i];
                }
            }
        }

        for(i=0;i<=k;i++) {

            int temp = n - pre2[i] - pre1[i];
            if(temp < res) {
                res = temp;
            }

        }


        return res;


    }
};
