class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> umap;
        vector<int> v(k+2,0);
        for(int x=0;x<n/2;x++){
            int a = nums[x];
            int b = nums[n-x-1];
            umap[abs(a-b)]++;          
            int maxi = abs(a-b) + max( (k-max(a,b)) , (min(a,b)) );
            v[maxi+1] += 1;
        }
        int prev = n/2;
        for(int x=0;x<=k;x++){
            v[x] = prev - v[x];
            prev = v[x];
        }
        int ans = INT_MAX;
        for(auto &x:umap){
            int temp = (n/2) - v[x.first]; 
            // cout<<x.first<<" "<<temp<<endl;
            int cal = (temp*2) + ((n/2)-x.second-temp);
            ans = min(ans,cal);
        }
        return ans;
    }
};
