class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n= nums.size(),i,ans=1e5,cur=0,t;
        multiset <int> cnt;
        vector <int> mx(k+1);
        for(i=0;i<n/2;i++)
        {
            auto val = max({k-nums[i],k-nums[n-i-1],nums[i],nums[n-i-1]});
            mx[val]++;
            cnt.insert(abs(nums[i]-nums[n-i-1]));
        }
        for(i=0;i<=k;i++)
        {
            t = n/2 - cnt.count(i);
            t+=cur;
            ans = min(ans,t);
            cur+=mx[i];
        }
        return ans;

    }
};
