class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int maxFreq = -1;
        int n = nums.size();
        vector<int> line(k+2, 0);

        for(int i=0; i<n/2; i++) {
            int a = nums[i];
            int b = nums[n-i-1];

            int trueDiff = abs(a-b);
            int maxDiff = max(max(a, b), k - min(a, b));

            line[0]++;
            line[trueDiff]--;
            line[trueDiff+1]++;
            line[maxDiff+1]++;
        }

        int ans = line[0];
        for(int i=1; i<line.size(); i++) {
            line[i]+= line[i-1];
            ans = min(ans, line[i]);
        }

        return ans;
    }
};
