class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int,int> mpp;
        vector<int> v;
        for(int i=0;i<a.size()/2;i++){
            v.push_back(max((k-min(a[i],a[n-i-1])),max(a[i],a[n-i-1])));
            mpp[abs(a[i]-a[n-i-1])]++;
        }
        int ans = INT_MAX;
        sort(v.begin(),v.end());
        // for(auto it:v) cout<<it<<" ";
        // cout<<endl;
        for(int i=0;i<=k;i++){
            int lb = lower_bound(v.begin(),v.end(),i)-v.begin();
            int cur = 2*lb+(n/2-lb);
            if(mpp.find(i)!=mpp.end()) cur-=mpp[i];
            ans=min(ans,cur);
            // cout<<i<<" "<<cur<<" "<<lb<<endl;
        }
        return ans;
    }
};
