class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> diff;
        // int i=0;int j = nums.size()-1;
        int maxi = INT_MAX;
        vector<int> changes(k+2,0);
        int i =0;int j = nums.size()-1;
        while (i<j){
            changes[0]++;
            int mini = min(nums[i],nums[j]);
            int m = max(nums[i],nums[j]);
            int maxi = max(m,k-mini);
            int diff=  abs(nums[i]-nums[j]);
            changes[diff]--;
            changes[diff+1]++;
            changes[maxi+1]++;
            i++;j--;
        }
        int sum = 0;
        for (int i=0;i<=k;i++){
            sum+= changes[i];
            maxi = min(sum,maxi);

        }
        return maxi;
    }
};
