/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function(nums, k) {
    const len = nums.length;
    const half = len >> 1;
    const map = new Map();
    let max = Infinity;
    for (let i = 0; i < half; ++i) {
        const right = len - 1 - i;
        const zero = Math.abs(nums[i] - nums[right]);
        const one = Math.max(Math.abs(nums[i] - 0), Math.abs(nums[i] - k), Math.abs(nums[right] - 0), Math.abs(nums[right] - k));
        const two = Math.max(nums[right], nums[i], k);
        map.set(0, (map.get(0) || 0) + 2);
        map.set(two + 1, (map.get(two + 1) || 0) - 2);
        map.set(0, (map.get(0) || 0) - 1);
        map.set(one + 1, (map.get(one + 1) || 0) + 1);
        map.set(zero, (map.get(zero) || 0) - 1);
        map.set(zero + 1, (map.get(zero + 1) || 0) + 1);
        max = Math.min(max, two);
    }
    let res = Infinity;
    let cur = 0;
    for (let i = 0; i <= max; ++i) {
        cur += map.get(i) || 0;
        res = Math.min(res, cur);
    }
    return res;
};
