func minChanges(nums []int, k int) int {

	n := len(nums)

	diff := make([]int, k+2)

	cnts := map[int]int{}
	for l, r := 0, len(nums)-1; l < r; l, r = l+1, r-1 {
		d := max(k-min(nums[l], nums[r]), max(nums[l], nums[r]))
		diff[0] += 1
		diff[d+1] -= 1
		cnts[abs(nums[l]-nums[r])]++
	}

	for i := 1; i < len(diff); i++ {
		diff[i] += diff[i-1]
	}

	ans := math.MaxInt >> 1
	half := n / 2
	for x, c := range cnts {
		ans = min(ans, (half-diff[x])*2+(diff[x]-c))
	}

	return ans

}

func abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}
