class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int ans = 0;
        int t = 1e9;
        int n = a.size();
        vector<int> b(k+1);

        for (int i = 0; i<n/2; ++i) {
            int x = abs(a[i] - a[n-1-i]);
            int mx = max(a[i],a[n-1-i]);
            mx = max(mx, k-a[i]);
            mx = max(mx, k-a[n-1-i]);
            b[mx] += 1;
            b[x] -= 1;
            if (x > 0 ) {
                b[x-1] += 1;
            }
            if (mx != k) {
                b[k] += 2;
                b[mx] -= 2;
            }

        }

        for (int i = k-1; i>=0; --i) {
            b[i] += b[i+1];
        }

        for (int i = 0; i<=k; ++i) {
            if (b[i] < t) {
                t = b[i];
                ans = i;
            }
        }
        return t;
    }
};
