class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,int>mp;
        int n=a.size();
        if(a.size()==2){
            return 0;
        }
        vector<int>mx;
        for(int i=0;i<n/2;i++){
            mp[abs(a[i]-a[n-1-i])]++;
            mx.push_back(max(max(a[i],a[n-1-i]),k-min(a[i],a[n-1-i])));
        }
        sort(mx.begin(),mx.end());
        // for(auto i:mx){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        int mn=INT_MAX;
        for(int i=0;i<=k;i++){
            int j=lower_bound(mx.begin(),mx.end(),i)-mx.begin();
            int len=j;
            int e=mx.size()-j;
            mn=min(mn,len*2+e-mp[i]);
            // cout<<i<<" "<<len*2+e-mp[i]<<" "<<j<<endl;
        }
        return mn;
    }
};
