class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n=len(nums)//2
        nums=[sorted([nums[i],nums[-i-1]]) for i in range(n)]
        det=[x[1]-x[0] for x in nums]
        # print(nums,det)
        cnt=sorted(list(Counter(det).items()),key=lambda x: x[1],reverse=1)
        res=2*n
        for (d,ct) in cnt:
            add=0
            for i in range(n):
                if det[i]!=d and not (nums[i][0]+d<=k or 0<=nums[i][1]-d<=k):
                    if d<=k:
                        add+=1
                    else:
                        add=-1
                        break
            if add==0: return min(res,n-ct)
            elif add!=-1: res=min(res,n-ct+add)
        return res
