class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int pairs = nums.size()/2;
        vector<int> cnt(k+1, 0);
        vector<int> mxx(k+2, 0);
        for(int i = 0, j = nums.size()-1; i < j; i++, j--) {
            int c = abs(nums[i] - nums[j]);
            int x = max(max(nums[i], nums[j])+1, k+1-min(nums[i], nums[j]));
            // cout << nums[i] << ", " << nums[j] << " : " << c << ", " << x << endl;
            cnt[c]++;
            mxx[x]++;
        }
        int res = INT_MAX;
        for(int i = 0; i <= k; i++) {
            if(i) mxx[i] += mxx[i-1];
            // cout << i << " : " << cnt[i] << ", " << mxx[i] << endl;
            res = min(res, pairs-cnt[i] + mxx[i]);
        }
        return res;
    }
};
