class Solution {
private:
    static bool comp(pair<int,int>&a,pair<int,int>&b){
        if(a.second==b.second)return a.first<b.first;
        return a.second>b.second;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int n=nums.size();
        for(int i=0; i<n/2; i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        if(mp.size()==1)return 0;
        vector<pair<int,int>>v;
        for(auto it=mp.begin(); it!=mp.end(); it++){
            v.push_back({it->first,it->second});
        }
        sort(v.begin(),v.end(),comp);

        int ans=INT_MAX;
        for(int i=0; i<v.size(); i++){
            if(i>0 && v[i].second==v[i-1].second)continue;
            int diff=v[i].first;
            int count=0;
            for(int i=0; i<n/2; i++){
                if(abs(nums[i]-nums[n-i-1])==diff)continue;
                else {
                    int mini=min(nums[i],nums[n-i-1]);
                    int maxi=max(nums[i],nums[n-i-1]);
                    if(maxi>=diff)count++;
                    else if(mini+diff<=k)count++;
                    else count+=2;
                }
            }
            ans=min(ans,count);
        }

        return ans;
    }
};
