class Solution {
public:
    int minChanges(vector<int>& A, int k) {
        map<int,int>mp;
        int maxi=0,cur=-1;
        vector<int>st;
        for(int i=0;i<A.size()/2;i++){
            int x = abs(A[i]-A[A.size()-1-i]);
            mp[x]++;
            if(maxi<mp[x]) maxi=mp[x],cur = x;
            int t = max({A[i],k-A[i],A[A.size()-1-i],k-A[A.size()-1-i]});
            st.push_back(t);
        }
        int ans = INT_MAX;
        sort(st.begin(),st.end());
        // for(int i=0;i<A.size()/2;i++){
        //     int x = abs(A[i]-A[A.size()-1-i]);
        //     if(x!=cur){
        //         ans++;
        //         if(A[i]>=cur||k-A[i]>=cur) continue;
        //         cout<<A[i]<<" ";
        //         if(A[A.size()-1-i]>=cur||k-A[A.size()-1-i]>=cur) continue;
        //         cout<<A[A.size()-i-1]<<endl;
        //         ans++;
        //     }   
        // }
        for(auto i:mp){
            int diff = i.first,c=i.second;
            int cnt = lower_bound(st.begin(),st.end(),diff)-st.begin();
            ans = min(ans,int(A.size()/2)-c+cnt);
        }
        return ans;
    }
};
