class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        unordered_map<int,int>mp;
        int n = nums.size();
        vector<int>l;
        for(int i=0;i<nums.size()/2;i++)
        {

            mp[abs(nums[i]-nums[n-1-i])]++;

            int ma = max(nums[i],nums[n-1-i]);
            int mi = min(nums[i],nums[n-1-i]);
            int limit = max(ma,k-mi);

            l.push_back(limit);

        }

        sort(l.begin(),l.end());
        int ans = n/2;
        for(int i=0;i<=k;i++)
        {
            int ind = lower_bound(l.begin(),l.end(),i)-l.begin();
            int left = ind;
            int right = (n/2)-ind;
            right-=mp[i];
            ans = min(ans,left*2 + right);
        }
        return ans;
    }
};
