class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n = v.size();
        const int SZ = 100002;
        vector<int> cum(SZ+1, 0);
        vector<int> pcum(SZ+1, 0);
        for (int i = 0; i < n/2; i++) {
            int a = v[i];
            int b = v[n-1-i];
            if (a > b) swap(a, b);
            int diff = b - a;
            int tot = max(b, k-a); 
            cum[0]++;
            cum[tot+1]--;
            pcum[0]++;
            pcum[tot+1]--;
            if (diff <= tot) {
                pcum[diff]--;
                pcum[diff+1]++;
            }
            if (k > tot) {
                cum[tot+1]++;
                cum[k+1]--;
                pcum[tot+1] += 2;
                pcum[k+1] -= 2;
                if (diff > tot) {
                    pcum[diff] -= 2;
                    pcum[diff+1] += 2;
                }
            }
        }
        int cans = 0;
        int cost = 0;
        int ans = 1e9;
        for (int i = 0; i < SZ; i++) {
            cans += cum[i];
            cost += pcum[i];
            if (cans == n/2) {
                ans = min(ans, cost);
            }
        }
        return ans;
    }
};
