class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector <int> v;
        priority_queue <int> q;
        map <int, int> one, two, cnt;
        for(int i = 0; i < n / 2; ++i) {
            int x = nums[i];
            int y = nums[n - i - 1];
            if (x < y) swap(x, y);
            v.push_back(max(x, k - y));
            if (abs(x - y) > max(x, k - y)) {
                two[abs(x - y)]++;
            }else {
                one[abs(x - y)]++;
            }
            cnt[abs(x - y)]++;
        }
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        int mx = INT_MAX, ptr = 0;
        for(int i = k; i >= 0; --i) {
            while (ptr < (int)v.size() && v[ptr] >= i) {
                ptr++;
            }
            int o = ptr;
            int t = (int)v.size() - ptr;
            //cout << i << ' ' << o << ' ' << t << endl;
            mx = min(mx, (t - two[i]) * 2 + (o - one[i]));
        }
        return mx;
    }
};
