func abs(a int) int {
    if a < 0 {
        return -a
    }
    return a
}

func minChanges(nums []int, k int) int {
    counters := make(map[int]int)
    for i := 0; i < len(nums); i++ {
        counters[abs(nums[i] - nums[len(nums)-i-1])]++
    }

    bestForCount := make(map[int]int)
    for k, v := range counters {
        //fmt.Println(k, v)
        if _, ok := bestForCount[v]; !ok {
            bestForCount[v] = k
            continue
        }
        if bestForCount[v] > k {
            bestForCount[v] = k
        }
    }

    //fmt.Println("-------")

    ans := len(nums)
    for _, v := range bestForCount {
        tmp := 0
        for i := 0; i < len(nums) / 2; i++ {
            //fmt.Println(i, nums[i], nums[len(nums)-i-1])
            if abs(nums[i] - nums[len(nums)-i-1]) == v {
                continue
            }
            if nums[i] - v >= 0 {
                tmp++
                //fmt.Println("inc")
                continue
            }
            if nums[i] + v <= k {
                tmp++
                //fmt.Println("inc")
                continue
            }
            if nums[len(nums)-i-1] - v >= 0 {
                tmp++
                //fmt.Println("inc")
                continue
            }
            if nums[len(nums)-i-1] + v <= k {
                tmp++
                //fmt.Println("inc")
                continue
            }
            tmp += 2
            //fmt.Println("double inc")
            continue
        }
        if tmp < ans {
            ans = tmp
        }
        //fmt.Println("-", key, v, tmp)
    }

    return ans
}
