class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>maxdiff;
        unordered_map<int,int>diff;
        int ans=1e9;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int maxv=max(nums[i],nums[n-i-1]);
            int minv=min(nums[i],nums[n-i-1]);
            int change=max(k-minv,maxv);
            diff[maxv-minv]++;
            maxdiff.push_back(change);
        }
        sort(maxdiff.begin(),maxdiff.end());
        for(auto x:diff){
            int oneop=maxdiff.end()-lower_bound(maxdiff.begin(),maxdiff.end(),x.first);
            int twoop=maxdiff.size()-oneop;
            oneop-=x.second;
            ans=min(ans,oneop+2*twoop);
            // cout<<oneop+2*twoop<<"--"<<x.first<<endl;
        }
        return ans;
    }
};
