class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> cnt(k + 1, 0);
        vector<int> add(k + 2, 0);
        int n = (int) a.size();
        for (int i = 0; i < n / 2; ++i) {
            ++cnt[abs(a[i] - a[n - i - 1])];
            int x = max(max(a[i], k - a[i]), max(a[n - i - 1], k - a[n - i - 1]));
            ++add[x + 1];
        }
        int ans = n / 2;
        int tmp = 0;
        for (int i = 0; i <= k; ++i) {
            tmp += add[i];
            ans = min(ans, n / 2 - cnt[i] + tmp);
        }
        return ans;
    }
};
