class Solution {


public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>sum(k+5,0);
        int n = nums.size();
        for(int i = 0;i<n/2;i++){
            int j = n-1-i;
            int a = min(nums[i],nums[j]);
            int b = max(nums[j],nums[i]);
            int up = max(k-a,b);
            int low = b-a;
            //[0,low)  (low,up] +1
            //(up,k] +=2
            if(low > 0){
                sum[0]+= 1;
                sum[low] -= 1;
            }
            if(up > low){
                sum[low+1]+=1;
                sum[up+1]-=1;
            }
            if(k > up){
                sum[up+1] += 2;
                sum[k+1] -= 2;
            }


        }
        int ret = INT_MAX;
        int val = 0;
        for(int i = 0;i<=k;i++){
            val += sum[i];
            ret = min(ret,val);
        }
        return ret;
    }
};
