typedef pair<ll,ll> pll;
typedef vector<string> vs;
const char en = '\n';

class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        vi sum(k+10);
        FOR(i, sz(v)/2) {
            int x = v[i];
            int y = v[sz(v)-1-i];
            if (x > y) swap(x,y);
            sum[0] += 1;
            sum[max(y,k-x)+1]+=1;
            sum[y-x] -= 1;
            sum[y-x+1] += 1;
        }
        for(int i=1;i<sz(sum);++i) {
            sum[i] += sum[i-1];
        }
        int ret = sz(v);
        for (int i=0;i<=k;++i) {
            ret = min(ret, sum[i]);
        }
        return ret;
    }
};
